import React, { useState, useEffect } from "react";
import { Route } from "react-router-dom";
import axios from "axios";
import ExperienceCard from "./ExperienceCard";

// set experience as all on page load and then when user selects, set state and make dynamic

function ExperienceListHome() {

    const [experiences, setExperiences] = useState([])
    // const [type, setType] = useState('')

    const getData = () => {

        axios
        .get(`https://atoz-backend.herokuapp.com/api/experiences`)
        .then(r => {
            setExperiences([r.data.experiences])
        })
        .catch( e => console.log('Axios Experience GET Error:', e))

    };

    useEffect(getData, [])

    const list = function() {
        experiences.map( arr => {
            arr.map( e => {
                return e
            })
        })
    }

    if (experiences.length >= 1) {
        return (
            <div>
                <Route 
                    exact path="/" 
                    render={props => <ExperienceCard {...props} ex={list()} />}
                />
                <Route 
                    exact path="/adventures" 
                    render={props => <ExperienceCard {...props} ex={list()} />}
                />
                <Route 
                    exact path="/food-tours" 
                    render={props => <ExperienceCard {...props} ex={list()} />}
                />
                <Route 
                    exact path="/cooking" 
                    render={props => <ExperienceCard {...props} ex={list()} />}
                />
                <Route 
                    exact path="/surfing" 
                    render={props => <ExperienceCard {...props} ex={list()} />}
                />
                <Route 
                    exact path="/concerts" 
                    render={props => <ExperienceCard {...props} ex={list()} />}
                />
                <Route 
                    exact path="/socail-impact" 
                    render={props => <ExperienceCard {...props} ex={list()} />}
                />
            </div>
        );
    }
    else {
        return (
            <div>
                <div>
                    There are no experiences in your area yet!
                </div>
            </div>
        );
    }
}

export default ExperienceListHome;
